package helperclasses;

public class ContactPerson {
	
	private String name1;
	private String phoneno1;
	private String name2;
	private String phoneno2;
	private String name3;
	private String phoneno3;
	
	
	public ContactPerson(String name1, String phoneno1, String name2,
			String phoneno2, String name3, String phoneno3) {
		this.setName1(name1);
		this.setPhoneno1(phoneno1);
		this.setName2(name2);
		this.setPhoneno2(phoneno2);
		this.setName3(name3);
		this.setPhoneno3(phoneno3);
		
		// TODO Auto-generated constructor stub
	}
	public String getName1() {
		return name1;
	}
	public void setName1(String name1) {
		this.name1 = name1;
	}
	public String getPhoneno1() {
		return phoneno1;
	}
	public void setPhoneno1(String phoneno1) {
		this.phoneno1 = phoneno1;
	}
	public String getName2() {
		return name2;
	}
	public void setName2(String name2) {
		this.name2 = name2;
	}
	public String getPhoneno2() {
		return phoneno2;
	}
	public void setPhoneno2(String phoneno2) {
		this.phoneno2 = phoneno2;
	}
	public String getName3() {
		return name3;
	}
	public void setName3(String name3) {
		this.name3 = name3;
	}
	public String getPhoneno3() {
		return phoneno3;
	}
	public void setPhoneno3(String phoneno3) {
		this.phoneno3 = phoneno3;
	}
	
	

}
